import E from 'wangeditor';
import * as imageConversion from "image-conversion";
export default {
  name: "ProgressAdd",

  data() {
    return {
      editorContent: '',
      editor: null,
      progress: {
        headPic: "",
        caption: "",
        info: "",
        content: ""
      }
    };
  },

  methods: {
    initProgress() {
      const path = this.$route.path;
      const arr = path.split("/");
      const projectId = arr[4];

      if (projectId) {
        console.log(projectId);
        this.axios.get(`/api/progress/${projectId}`).then(({
          data
        }) => {
          this.progress = data.data;
          this.editor.txt.html('<p>' + data.data.content + '</p>');
        });
      }
    },

    saveOrUpdate() {
      this.progress.content = this.editor.txt.html();
      this.axios.post("/api/progress/saveOrUpdate/${projectId}", this.progress).then(({
        data
      }) => {
        if (data.flag) {
          this.$message.success(data.message);

          _this.$router.back(-1);
        }
      });
    },

    beforeUpload(file) {
      return new Promise(resolve => {
        if (file.size / 1024 < 200) {
          resolve(file);
        } // 压缩到200KB,这里的200就是要压缩的大小,可自定义


        imageConversion.compressAccurately(file, 200).then(res => {
          resolve(res);
        });
      });
    },

    uploadCover(response) {
      console.log(response.data);
      this.progress.headPic = response.data;
    }

  },

  created() {
    this.initProgress();
  },

  mounted() {
    this.editor = new E(this.$refs.editor);
    this.editor.customConfig = this.editor.customConfig ? this.editor.customConfig : this.editor.config;

    this.editor.customConfig.onchange = html => {
      // console.log(html,'----------------',a)
      this.editorContent = html;
    };

    this.editor.customConfig.uploadFileName = 'file'; //设置文件上传的参数名称

    this.editor.customConfig.uploadImgServer = '/api/file/editorImg'; // 上传图片到服务器

    this.editor.customConfig.showLinkImg = false; // 隐藏“网络图片”tab

    this.editor.create();
  }

};
