import axios from "axios";
import '../../assets/css/login.css';
import '../../assets/js/login';
export default {
  name: "login",
  data: function () {
    return {
      loginForm: {
        email: "",
        password: "",
        key: "",
        verKey: ""
      },
      verifyCode: ""
    };
  },

  created() {
    this.getVerifyCode();
  },

  methods: {
    getVerifyCode() {
      axios.get("http://localhost:10010/captcha").then(({
        data
      }) => {
        console.log(data.data);
        this.verifyCode = data.data;
      });
    },

    login() {
      const _this = this;

      axios.post("http://localhost:10010/home/homeUser/login", _this.loginForm).then(({
        data
      }) => {
        if (data.flag) {
          console.log("登陆成功");

          _this.$message.success(data.message); //_this.$router.push({ path: "/" });

        }
      });
    }

  }
};
